import React, { useState } from "react";
import { useParams } from "react-router-dom";
import "../page/style/Header.css";

function Header(props) {
  const { id } = useParams();
  const [item, setItem] = useState([
    { name: "홈", id: 0 },
    { name: "시리즈", id: 1 },
    { name: "영화", id: 2 },
    { name: "NEW! 요즘 대세 콘텐츠", id: 3 },
  ]);
  console.log(id);

  const list = item.map((data) => {
    if (data.id == id) {
      return <div className="selected">{data.name}1</div>;
    } else {
      return <div>{data.name}</div>;
    }
  });
  return (
    <div className="Header">
      <div className="header-left">
        <div className="logo">logo</div>
        <div className="name">
          Nerflix <br /> Community
        </div>
      </div>
      <div className="header-middle">
        {/* <div className="item1">홈</div>
        <div className="item2">시리즈</div>
        <div className="item3">영화</div>
        <div className="item4">NEW! 요즘 대세 콘텐츠</div> */}
        {list}
      </div>
      <div className="header-right">
        <div></div>
        <div></div>
      </div>
    </div>
  );
}

export default Header;
