import axios from "axios";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import "../page/style/PostDoor.css";

function PostDoor({ title, num }) {
  const { page } = useParams();
  const boardId = 10 * page + num;
  const [list, setList] = useState();
  useEffect(() => {
    axios
      .get("http://localhost:3001/home")
      .then((res) => {
        setList([...res.data.list]);
        console.log(list[1]);
      })
      .catch((err) => {
        console.log("에러 발생", err);
      });
  }, [boardId]);

  const rendering = () => {
    const _list = [];
    for (let i = 0; i < 4; i++) {
      _list.push(
        <div className="post">
          <div className="content">{list[i].title}</div>
          <div className="when">{list[i].date}</div>
        </div>
      );
    }
  };

  return (
    <div className="PostDoor">
      <div className="name">
        {title}
        {boardId}
      </div>
      <div className="post">
        <div className="content">list</div>
        <div className="when">방금</div>
      </div>
      <div className="post">
        <div className="content">post2 포스트2</div>
        <div className="when">방금</div>
      </div>
      <div className="post">
        <div className="content">post3 포스트3</div>
        <div className="when">방금</div>
      </div>
      <div className="post">
        <div className="content">post4 포스트4</div>
        <div className="when">방금</div>
      </div>
    </div>
  );
}

export default PostDoor;
