import axios from "axios";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import "../page/style/PostDoor.css";

function PostDoor({ title, num }) {
  const { page } = useParams();
  const boardId = 10 * page + num;
  const [list, setList] = useState("");
  useEffect(() => {
    axios
      .get("http://localhost:3001/home")
      .then((res) => {
        console.log(res.data);
        setList(res.data);
      })
      .catch((err) => {
        console.log("에러 발생", err);
      });
  }, [boardId]);

  return (
    <div className="PostDoor">
      <div className="name">
        {title}
        {boardId}
      </div>
      <div className="post">
        <div className="content">{list[0].title}</div>
        <div className="when">{list[0].date}</div>
      </div>
      <div className="post">
        <div className="content">{list[1].title}</div>
        <div className="when">{list[1].date}</div>
      </div>
      <div className="post">
        <div className="content">{list[2].title}</div>
        <div className="when">{list[2].date}</div>
      </div>
      <div className="post">
        <div className="content">{list[3].title}</div>
        <div className="when">{list[3].date}</div>
      </div>
    </div>
  );
}

export default PostDoor;
