import "../page/style/PostDoor.css";

function PostDoor({ title, num }) {
  const { page } = useParams();
  const boardId = useState(10 * page + num);
  const [list, setList] = useState();
  const [result, setResult] = useState();
  useEffect(() => {
    axios
      .get("http://localhost:3001/home", {
        params: boardId,
      })
      .then((res) => {
        const clone = [...res.data.list];
        setList(clone);
        console.log(list);
        setResult(rendering());
      })
      .catch((err) => {
        console.log("에러 발생b 시발", err);
      });
  }, []);

  useEffect(() => {
    rendering();
  }, [list]);

  const rendering = () => {
    const _list = [];
    console.log("rendering function 속 함수 ");
    if (list.length === 0) return;
    for (let i = 0; i < 4; i++) {
      _list.push(
        <div className="post">
          <div className="content">{list[i].title}</div>
          <div className="when">{list[i].date}</div>
        </div>
      );
    }
    return _list;
  };

  return (
    <div className="PostDoor">
