import { date } from "./date";
import { getUser } from "./user";

const inputform = async (title, content, page, boardid) => {
  const _user = await getUser();
  const _email = _user.email;
  const _date = await date();
  const document = {
    page: page,
    board_id: boardid,
    id: -1,
    title: title,
    content: content,
    writer: _user.email,
    user: _user,
    likes: 0,
    date: _date,
    comments: [],
  };
  return document;
};

export default inputform;
