import { render } from "@testing-library/react";
import axios from "axios";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import "../page/style/PostDoor.css";

function PostDoor({ title, num }) {
  const { page } = useParams();
  const _page = useState(page);
  const boardId = useState(10 * page + num);
  const [list, setList] = useState();
  const [result, setResult] = useState();
  useEffect(() => {
    axios
      .get("http://localhost:3001/home", {
        params: boardId,
      })
      .then((res) => {
        const clone = [...res.data.list];
        setList(clone);
        console.log(list[0]);
        setResult(rendering());
      })
      .catch((err) => {
        console.log("에러 발생b 시발", err);
      });
  }, [_page]);

  const rendering = (arr) => {
    return (
      <div className="post">
        <div className="content">{arr.title}</div>
        <div className="when">{arr.date}</div>
      </div>
    );
  };

  return (
    <div className="PostDoor">
      <div className="name">
        {title}
        {boardId}
      </div>
      {result}
    </div>
  );
}

export default PostDoor;

